CREATE TABLE `Taxpayer` (
  `TFN` varchar(15) PRIMARY KEY NOT NULL,
  `PersonID` varchar(20) NOT NULL,
  `FirstName` varchar(50) NOT NULL,
  `LastName` varchar(50) NOT NULL,
  `Email` varchar(100) UNIQUE NOT NULL
);

CREATE TABLE `Pay_Roll_Record` (
  `TaxpayerID` int NOT NULL,
  `PayPeriod` int NOT NULL,
  `PayDate` date NOT NULL,
  `TaxableIncome` decimal(10,2) NOT NULL,
  `NetIncome` decimal(10,2) NOT NULL,
  `TaxWithheld` decimal(10,2) NOT NULL
);



